import BigNumber from "bignumber.js";
export const serializeRippleTransaction = ({ family, fee, tag, amount, recipient, }) => {
    return {
        family,
        amount: amount.toString(),
        recipient,
        fee: fee ? fee.toString() : undefined,
        tag,
    };
};
export const deserializeRippleTransaction = ({ family, fee, tag, amount, recipient, }) => {
    return {
        family,
        amount: new BigNumber(amount),
        recipient,
        fee: fee ? new BigNumber(fee) : undefined,
        tag,
    };
};
